import React, { useState } from 'react';
import styles from "./searchnftcss.module.css";


function SearchNFT(){
    const [nftList, setnftList] = useState(["a NFT", "b NFT", "c NFT", "dnft","c","e","f","e","f","e"]);
    const [nftIndex, setnftIndex] = useState(0);
    const RenderNFTList = () => {
        return nftList.map((nft, idx) => (
                <div key = {idx} className = {styles.nftcomponent} onClick = {() => setnftIndex(idx)}>
                    {nft}
                </div>
            ))
    }
    return (
        <div className = {styles.searchnft}>
            <div className={styles.nftinfo}>
                <div className={styles.nftmetadata}>
                {nftList[nftIndex]}의 메타데어터
                </div>
                <button className={styles.buybutton}>Buy NFT</button>
            </div>
            <div className={styles.search}>
                <div className={styles.inputcontainer}>
                    <input className = {styles.input}>

                    </input>
                    <button className = {styles.searchbutton}>
                        Search
                    </button>
                </div>
                <div className={styles.searchlist}>
                    {RenderNFTList()}
                </div>
            </div>
        </div>
    );
}

export default SearchNFT;
