import React, { useState } from 'react';
import styles from "./mynftcss.module.css";


function MyNFT(){
    const [nftList, setnftList] = useState(["a NFT", "b NFT", "c NFT", "dnft","c","e","f","e","f","e"]);
    const [nftIndex, setnftIndex] = useState(0);
    const RenderNFTList = () => {
        return nftList.map((nft, idx) => (
                <div key = {idx} className = {styles.nftcomponent} onClick = {() => setnftIndex(idx)}>
                    {nft}
                </div>
            ))
    }
    return (
        <div className={styles.mynft}>
            <div className={styles.nftinfo}>
                {nftList[nftIndex]}
                <div>- 이 nft의 db정보가 포함된 통합 nft</div>
                <div>- 배당률 (%)</div>
                <div>- 수익 (원)</div>
            </div>
            <div className={styles.nftlist}>
                <div className = {styles.title}>NFT LIST</div>
                {RenderNFTList()}
            </div>
        </div>
    );
}

export default MyNFT;
